#include "DeviceGUI.h"
#include "DisplayDriver.h"
#if LV_USE_LIBINPUT
#include "LinuxInputDriver.h"
#elif defined(T_DECK)
#include "TDeckInputDriver.h"
#else
#include "InputDriver.h"
#endif
#include "ILog.h"
#include "lvgl.h"
#include "ui.h" // this is the lvgl ui generated by squareline editor; set build flag -I to its location in the platformio.ini variant

DeviceGUI::DeviceGUI(DisplayDriver *driver) : displaydriver(driver), inputdriver(nullptr)
{
#if LV_USE_LIBINPUT
inputdriver = new LinuxInputDriver;
#elif defined(T_DECK)
inputdriver = new TDeckInputDriver;
#endif

}

void DeviceGUI::init(IClientBase *client)
{
    displaydriver->init(this);
    if (inputdriver)
        inputdriver->init();
    ILOG_DEBUG("ui_init...\n");
    ui_init();
}

void DeviceGUI::task_handler(void)
{
    displaydriver->task_handler();
};

DeviceGUI::~DeviceGUI() {
    delete inputdriver;
}